import { useState } from "react";
import { useForm } from "react-hook-form";

function HealthDeclarationForm() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const [symptoms, setSymptoms] = useState([]);

  const onSubmit = (data) => {
    console.log(data);
  };

  const handleSymptomChange = (event) => {
    const symptom = event.target.value;
    const checked = event.target.checked;
    if (checked) {
      setSymptoms([...symptoms, symptom]);
    } else {
      setSymptoms(symptoms.filter((item) => item !== symptom));
    }
  };

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      style={{
        alignItems: "center",
      }}
    >
      <label>
        Tên:
        <input type="text" {...register("name", { required: true })} />
        {errors.name && <span>This field is required</span>}
      </label>
      <label>
        Địa chỉ:
        <input type="text" {...register("address", { required: true })} />
        {errors.address && <span>This field is required</span>}
      </label>
      <label>
        Số điện thoại:
        <input
          type="tel"
          {...register("phone", { required: true, pattern: /^[0-9]*$/ })}
        />
        {errors.phone && errors.phone.type === "required" && (
          <span>This field is required</span>
        )}
        {errors.phone && errors.phone.type === "pattern" && (
          <span>Invalid phone number</span>
        )}
      </label>
      <label>
        Email:
        <input
          type="email"
          {...register("email", { required: true, pattern: /^\S+@\S+$/i })}
        />
        {errors.email && errors.email.type === "required" && (
          <span>This field is required</span>
        )}
        {errors.email && errors.email.type === "pattern" && (
          <span>Invalid email address</span>
        )}
      </label>
      <label>
        Trong vòng 14 ngày qua, bạn có dấu hiệu triệu chứng gì không?
      </label>
      <label>
        Sốt:
        <input type="checkbox" value="Fever" onChange={handleSymptomChange} />
      </label>
      <label>
        Ho:
        <input type="checkbox" value="Cough" onChange={handleSymptomChange} />
      </label>
      <label>
        Khó thở:
        <input
          type="checkbox"
          value="Shortness of breath"
          onChange={handleSymptomChange}
        />
      </label>
      <label>
        Đau đầu:
        <input
          type="checkbox"
          value="Headache"
          onChange={handleSymptomChange}
        />
      </label>
      <label>
        Đau họng:
        <input
          type="checkbox"
          value="Sore throat"
          onChange={handleSymptomChange}
        />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}
export default HealthDeclarationForm;
