import React from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";

const initialValues = {
  to: "",
  subject: "",
  message: "",
  attachments: [],
};

const validationSchema = Yup.object().shape({
  to: Yup.string().email("Invalid email address").required("Required"),
  subject: Yup.string().required("Required"),
  message: Yup.string().required("Required"),
  attachments: Yup.array().max(3, "Maximum 3 files allowed"),
});

const onSubmit = (values) => {
  console.log(values);
  // Handle submit logic here
};

const EmailForm = () => {
  return (
    <Formik
      initialValues={initialValues}
      validationSchema={validationSchema}
      onSubmit={onSubmit}
    >
      {({ values, errors, touched, handleSubmit, setFieldValue }) => (
        <Form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="to">To:</label>
            <Field name="to" type="email" />
            <ErrorMessage name="to" />
          </div>
          <div>
            <label htmlFor="subject">Subject:</label>
            <Field name="subject" type="text" />
            <ErrorMessage name="subject" />
          </div>
          <div>
            <label htmlFor="message">Message:</label>
            <Field name="message" as="textarea" />
            <ErrorMessage name="message" />
          </div>
          <div>
            <label htmlFor="attachments">Attachments:</label>
            <Field
              name="attachments"
              type="file"
              multiple
              onChange={(event) => {
                setFieldValue("attachments", event.currentTarget.files);
              }}
            />
            <ErrorMessage name="attachments" />
          </div>
          <button type="submit">Send</button>
        </Form>
      )}
    </Formik>
  );
};

export default EmailForm;
